const imports = async () => {
    const start = moment()
    console.log('start import')
   
    await importKDK()

    await importData()
   
    await importPOD()

    const diff = moment(start).diff(moment())
    console.log('Import end in: '+diff*(-1)+'ms')
}

imports()



